import React, { useEffect } from "react";
import { useState,useRef } from "react";
import { LocalStorage } from "./components/LocalStorage";
import Editor from "./components/Editor";
import { AiFillHtml5 } from "react-icons/ai";
import { DiCss3, DiJavascript } from "react-icons/di";
import { GrCodepen } from "react-icons/gr";
import "./components/editor.scss";
import Split from "react-split";

function App() {
  const [html, setHtml] = LocalStorage("html", "");
  const [css, setCss] = LocalStorage("css", "");
  const [js, setJs] = LocalStorage("js", "");
  const [srcDoc, setsrcDock] = useState("");
  const ref = useRef()
  const elementRef = useRef(null)
  const [width, setWidth] = useState({
    winWidth:window.innerWidth,
  });
  const detectSize = () =>{
    setWidth({
      winWidth:window.innerWidth
    })
  }
  useEffect(()=>{
       window.addEventListener('resize',detectSize)
       return()=>{window.removeEventListener('resize',detectSize)} 
  },[width])

  console.log(width.winWidth)






  useEffect(() => {
    const timeout = setTimeout(() => {
      setsrcDock(
        `
            <html>
             <body>${html}</body>
             <style>${css}</style>
             <script>${js}</script>
            </html>
            `
      );
    }, 250);

    return () => clearTimeout(timeout);
  }, [html, css, js]);

  function onIframe(){
    document.querySelector('iframe').contentWindow.document.querySelector('html').style.overflowWrap = "break-word"
    } 





  return (
    <>
      <header className="" ref={elementRef}>
