import React from 'react'
// import './editor.scss'
import 'codemirror/lib/codemirror.css'
import 'codemirror/theme/material-ocean.css'
import 'codemirror/theme/ayu-mirage.css'
import 'codemirror/mode/xml/xml'
import 'codemirror/mode/javascript/javascript'
import 'codemirror/mode/css/css'
import {Controlled } from 'react-codemirror2'
import { useRef } from 'react'
import {AiFillHtml5} from 'react-icons/ai'
// import {FaCompressArrowsAlt} from 'react-icons/fa' 





const Editor = (props) => {
    const editors = useRef()
    const wrapper = useRef()

    const{
        language,
        logo,
        value,
        onChange,
        color,
        title

    } = props

    function handleChange(editor, data,value) {
        onChange(value)
    }

    const editorWillUnmount = () => {
        editors.current.display.wrapper.remove()
        wrapper.current.hydrated = false
      }

  return (
    <>
    <div className="editor-container ">
        <div className="editor-title text-white flex justify-between">
            <div className='title flex items-baseline px-7 py-2 text-2xl' style={{color:color}} >
            {logo} { title}
            </div>
            <button className='background px-5 py-2'>
                {/* <FaCompressArrowsAlt/> */}
                click
            </button>
        </div>
        <Controlled
        onBeforeChange={handleChange}
         value={value}
         className='code-mirror-wrapper '
         options={{
            lineWrapping: true,
            lint: true,
            mode: language,
            theme: "ayu-mirage",
            lineNumbers:true,
         }}

         editorDidMount={(e) => editors.current = e}
         editorWillUnmount={editorWillUnmount}

        />
    </div>
    </>
  )
}

export default Editor
