import React, { useEffect } from 'react';
import { useState } from 'react';
import  {LocalStorage}  from './components/LocalStorage';
import Editor from './components/Editor';
import SplitPane from 'react-split-pane';

function App() {
  const [html,setHtml] = LocalStorage('html','')
  const [css,setCss] = LocalStorage('css','')
  const [js,setJs] = LocalStorage('js','')
  const [srcDoc,setsrcDock] = useState('')



  useEffect(()=>{
     const timeout = setTimeout(()=>{
           setsrcDock(
            `
            <html>
             <body>${html}</body>
             <style>${css}</style>
             <script>${js}</script>
            </html>
            `
           )
     },250)

     return () => clearTimeout(timeout)
  },[html,css,js])


  // const srcDoc = 


  return (
    <>
       <header className='p-5  bg-black text-white border-b  '>CodePen</header>
      <SplitPane className=" pane pane-top grid grid-cols-3 gap-5 bg-black p-3 " split = 'vertical' minSize={'50%'} >
         <Editor language='xml' displayName = 'HTML' value={html} onChange={setHtml}></Editor>
         <Editor language='css' displayName = 'CSS' value={css} onChange={setCss}></Editor>
         <Editor language='javascript' displayName = 'JS' value={js} onChange={setJs}></Editor>
      </SplitPane>
      <div className="pane-bottom  ">
         <iframe 
          srcDoc={srcDoc}
          title="This is a unique title"
          frameBorder="0"
          sandbox="allow-scripts"
          width="100%"
          height="100%"
          />
    </div>
    </>
  );
}

export default App;
