import React, { useEffect } from 'react';
import { useState } from 'react';
import Editor from './components/Editor';

function App() {
  const [html,setHtml] = useState('')
  const [css,setCss] = useState('')
  const [js,setJs] = useState('')


  useEffect(()=>{

  })


  const srcDoc =`
    <html>
     <body>${html}</body>
     <style>${css}</style>
     <script>${js}</script>
    </html>
    `


  return (
    <>
      <div className=" pane top-pan grid grid-cols-3 gap-5  ">
         <Editor language='xml' displayName = 'HTML' value={html} onChange={setHtml}></Editor>
         <Editor language='css' displayName = 'CSS' value={css} onChange={setCss}></Editor>
         <Editor language='javascript' displayName = 'JS' value={js} onChange={setJs}></Editor>
      </div>
      <div className="pane">
         <iframe title="This is a unique title"
         src={srcDoc}
          frameBorder="0"
          sandbox="allow-scripts"
          width="100%"
          height="100%"
          />
    </div>
    </>
  );
}

export default App;
