import React from 'react';
import { useState } from 'react';
import Editor from './components/Editor';

function App() {
  const [html,setHtml] = useState('')
  const [css,setCss] = useState('')
  const [js,setJs] = useState('')
  return (
    <div className="App">
      <div className=" pane top-pan flex">
         <Editor language='xml' dispalyName = 'HTML' value={html} onChange={setHtml}></Editor>
         <Editor language='xml' dispalyName = 'HTML' value={css} onChange={setCss}></Editor>
         <Editor language='xml' dispalyName = 'HTML' value={js} onChange={setJs}></Editor>
      </div>
      <div className="pane">
         <iframe title="This is a unique title"
          frameBorder="0"
          sandbox="allow-scripts"
          width="100%"
          height="100%"
          />
      </div>
    </div>
  );
}

export default App;
