import React, { useEffect } from "react";
import { useState } from "react";
import { LocalStorage } from "./components/LocalStorage";
import Editor from "./components/Editor";
import { AiFillHtml5 } from "react-icons/ai";
import { DiCss3, DiJavascript } from "react-icons/di";
import { GrCodepen } from "react-icons/gr";
import "./components/editor.scss";
import Split from "react-split";

import SplitPane, { Pane }  from "react-split-pane";  

function App() {
  const [html, setHtml] = LocalStorage("html", "");
  const [css, setCss] = LocalStorage("css", "");
  const [js, setJs] = LocalStorage("js", "");
  const [srcDoc, setsrcDock] = useState("");

  useEffect(() => {
    const timeout = setTimeout(() => {
      setsrcDock(
        `
            <html>
             <body>${html}</body>
             <style>${css}</style>
             <script>${js}</script>
            </html>
            `
      );
    }, 250);

    return () => clearTimeout(timeout);
  }, [html, css, js]);

  return (
    <>
      <header className="">
        <GrCodepen /> <div>Codepen</div>
      </header>
      <SplitPane split="vertical"  minSize={0}>

        <div className=" pane top-pan" style={{display:'flex',height:'50vh'}}>
          <Editor
            language="xml"
            color="#E96228"
            title="HTML"
            logo={<AiFillHtml5 />}
            value={html}
            onChange={setHtml}
          ></Editor>
          <Editor
            language="css"
            color="dodgerblue"
            title="CSS"
            logo={<DiCss3 />}
            value={css}
            onChange={setCss}
          ></Editor>
          <Editor
            language="javascript"
            color="orange"
            title="JS"
            logo={<DiJavascript />}
            value={js}
            onChange={setJs}
          ></Editor>
        </div>

        <div className="pane-bottom pane ">
          <iframe
            srcDoc={srcDoc}
            title="This is a unique title"
            frameBorder="0"
            sandbox="allow-scripts"
            width="100%"
            height="100%"            
          />
        </div>
      </SplitPane>
    </>
  );
}

export default App;
